ui:View.js:Script:
(function () {
    return {
        defaults:{
            tagName:'button',
            'class':'btn',
            href: null
        },
        _renderHref:function (href) {
            if (this.$tagName == "a") {
                this.$el.setAttribute("href", href || "#");
            }
        },
        _renderType:function (type, oldType) {
            if(oldType){
                $(this.$el).removeClass("btn-" + oldType);
            }
            if (type) {
                $(this.$el).addClass("btn-" + type);
            }
        }
    }
})();
